Ext.define('Security.view.RoleGrid', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.rolegrid',

    title: '角色管理',
    columnLines: true,
    store: 'Role',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            dockedItems: [
                {
                    xtype: 'pagingtoolbar',
                    dock: 'bottom',
                    displayInfo: true,
                    store: 'Role'
                },
                {
                    xtype: 'toolbar',
                    dock: 'top',
                    items: [
                        me.processMyTriggerField2({
                            xtype: 'triggerfield',
                            width: 220,
                            fieldLabel: '名称',
                            labelWidth: 40,
                            emptyText: '请输入一个名称！'
                        }),
                        {
                            xtype: 'tbseparator'
                        },
                        {
                            xtype: 'button',
                            text: '添加',
                            tooltip: '添加'
                        },
                        {
                            xtype: 'button',
                            text: '编辑',
                            tooltip: '编辑'
                        },
                        {
                            xtype: 'button',
                            text: '删除',
                            tooltip: '删除'
                        },
                        {
                            xtype: 'tbseparator'
                        },
                        {
                            xtype: 'button',
                            text: '角色授权'
                        }
                    ]
                }
            ],
            selModel: Ext.create('Ext.selection.CheckboxModel', {
                mode: 'SINGLE'
            }),
            columns: [
                {
                    xtype: 'gridcolumn',
                    dataIndex: 'name',
                    text: '名称'
                },
                {
                    xtype: 'gridcolumn',
                    dataIndex: 'code',
                    text: '代码'
                },
                {
                    xtype: 'gridcolumn',
                    dataIndex: 'description',
                    text: '描述',
                    flex: 1
                }
            ]
        });

        me.processRoleGrid(me);
        me.callParent(arguments);
    },

    processMyTriggerField2: function(config) {
        config.xtype = 'searchfield';
        config.paramName = 'search_name_like';
        config.store = Ext.StoreMgr.lookup('Role');

        return config;
    },

    processRoleGrid: function(config) {
        if (config.removeDockedItems) {
            config.selModel = Ext.create('Ext.selection.CheckboxModel');

            config.dockedItems = [{
                xtype: 'toolbar',
                items: [{
                    text: '添加',
                    tooltip: '添加'
                },{
                    text: '删除',
                    tooltip: '删除'
                }]
            }];

        }

        if (config.newStore) {
            config.store = Ext.create('Security.store.Role', {
                storeId: 'RoleStore',
                autoLoad: false,
                proxy: {
                    type: 'rest',
                    url: 'roles/findByUserId',
                    reader: {
                        type: 'json',
                        root: 'content',
                        totalProperty: 'totalElements'
                    }
                }
            });
        }
    }

});